import { Container, Col, Form, Row, Card, Dropdown } from "react-bootstrap";

import BackOfficeNav from "../BackOfficeNav";
import { useNavigate, useParams, Link } from "react-router-dom";
import * as Icon from "react-bootstrap-icons";

import { useSelector, useDispatch } from "react-redux";
import { format, compareAsc } from "date-fns";
import DatePicker from "react-datepicker";
import { useState, useEffect } from "react";
import { editTournament } from "../../../redux/actions";
const Structures = () => {
  const params = useParams();
  const dispatch = useDispatch();
  const tournamentData = useSelector((state) => state.tournaments.tournaments);
  const tournament = tournamentData.tournaments.find(
    (name) => name.name === params.tournament
  );
  const [isCheckIn, setIsCheckIn] = useState(false);
  const [checkInOpens, setCheckInOpens] = useState("");
  const [checkInCloses, setCheckInCloses] = useState("");
  const [key, setKey] = useState("activation");
  const [update, setUpdate] = useState(false);

  const checkInSettings = {
    participants: {
      isCheck_in: isCheckIn,
      checkInOpeningDate: checkInOpens.toString(),
      checkInClosingDate: checkInCloses.toString(),
    },
  };
  const handleUpdate = () => {
    checkInCloses && checkInOpens && isCheckIn
      ? setUpdate(true)
      : setUpdate(false);
    dispatch(editTournament(checkInSettings, tournament._id));
  };
  return (
    <Container fluid className="main-container textColor">
      <Row>
        <Col lg={2} className="px-0">
          {/* <BackOfficeNav data={tournament} page={"settings"} /> */}
        </Col>
        <Col lg={10} className="my-5 px-5">
          <Row>
            <h3 className="d-flex my-2">Structure</h3>
          </Row>
          <Row>
            <Col md={4}>
              <Card className="registration-card mx-auto">
                <Card.Header>
                  <h5>1. League</h5>
                </Card.Header>
                <span>League</span>
                <Card.Body>
                  <Link>
                    <span>
                      <Dropdown>
                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                          <span className="textColor3">Configure</span>
                          <Icon.ThreeDotsVertical size={20} color="#0cc4f2" />
                        </Dropdown.Toggle>

                        <Dropdown.Menu>
                          <Dropdown.Item>
                            <Icon.Gear size={20} color="#0cc4f2" />
                            <span className="textColor3 link-btm-bar">
                              Configure
                            </span>
                          </Dropdown.Item>
                          <Dropdown.Item className="my-2">
                            <Icon.Search size={20} color="#0cc4f2" />
                            <span className="textColor3 link-btm-bar">
                              Results
                            </span>
                          </Dropdown.Item>
                          <hr />
                          <Dropdown.Item>
                            <Icon.Trash size={20} color="red" />
                            <span className="text-danger link-btm-bar">
                              Delete
                            </span>
                          </Dropdown.Item>
                        </Dropdown.Menu>
                      </Dropdown>
                    </span>
                  </Link>
                </Card.Body>
              </Card>
            </Col>
          </Row>
        </Col>
      </Row>
    </Container>
  );
};
export default Structures;
