import { Col, Container, Row, Form } from "react-bootstrap";
import { Link } from "react-router-dom";
import logo from "../img/Blue_Futuristic_Gaming_Logo-removebg-preview.png";
import { useSelector, useDispatch } from "react-redux";
import { useState, useEffect } from "react";
import { logInData } from "../redux/actions";

const LogInPage = () => {
  const [formData, updateFormData] = useState("");

  const dispatch = useDispatch();
  const [name, setName] = useState("");
  const [surname, setSurname] = useState("");
  const [check, setCheck] = useState(false);
  const [password, setPassword] = useState("");
  const isLoading = useSelector((state) => state.giftData.isLoading);
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState(undefined);

  const handleName = (e) => {
    setName(e.target.value);
    dispatch(logInData(loginFormData));
  };

  const handleSurname = (e) => {
    setSurname(e.target.value);
    dispatch(logInData(loginFormData));
  };
  const handlePassword = (e) => {
    setPassword(e.target.value);
    dispatch(logInData(loginFormData));
  };
  const handleEmail = (e) => {
    setEmail(e.target.value);
    dispatch(logInData(loginFormData));
  };
  const handlePhone = (e) => {
    setPhone(e.target.value);
    dispatch(logInData(loginFormData));
  };
  const handleCheck = (e) => {
    setCheck(e.target.value);
    dispatch(logInData(loginFormData));
  };

  const loginFormData = {
    name: name,
    surname: surname,
    email: email,
    phone: phone,
    password: password,
    check: check,
  };
  return (
    <Container fluid className="login-page">
      <Row className="giftcard-preview-nav py-2 ">
        <Col className="d-flex flex-column ml-5">
          <Link className="mr-auto" to={"/"}>
            <img className="logo-img " src={logo} alt="" />
          </Link>
          <span className="mr-auto">LIVE EXPIRIENCE</span>
        </Col>
      </Row>
      <Row className="d-flex justify-content-center mt-5 textColor">
        <Col md={5} className=" login-content">
          <h1>Sign Up</h1>
          <span>Please fill in the form to create an account</span>
          <hr />
          <Form.Group className="d-flex">
            <Col>
              <Form.Control
                placeholder="First name"
                required
                // onChange={handleChange}
                onChange={handleName}
              />
            </Col>
            <Col>
              <Form.Control
                placeholder="Last name"
                required
                // onChange={handleChange}
                onChange={handleSurname}
              />
            </Col>
          </Form.Group>
          <Form.Group className="mb-3">
            <Col>
              {" "}
              <Form.Control
                type="email"
                placeholder=" Email"
                onChange={handleEmail}
              />
            </Col>
          </Form.Group>
          <Form.Group className="mb-3">
            <Col>
              <Form.Control
                type="password"
                placeholder=" Password"
                onChange={handlePassword}
              />
            </Col>
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Control
              type="password"
              placeholder=" Confirm Password"
              onChange={handlePassword}
            />
          </Form.Group>
        </Col>
      </Row>
    </Container>
  );
};
export default LogInPage;
